const mongoose = require('mongoose');

// This is the core of our AI system.
// We store the predictions generated by our Python engine here so the frontend can display them.
const predictionSchema = new mongoose.Schema({
    date: { type: Date, required: true }, // The date for which the surge is predicted
    predictedPatientCount: { type: Number, required: true },
    diseaseType: { type: String, required: true }, // e.g., 'Dengue', 'Viral Fever'
    confidenceScore: { type: Number }, // 0-100% confidence from the model
    contributingFactors: [{ type: String }], // Why do we think this will happen? (e.g., 'Festival', 'Weather')
    recommendedActions: [{ type: String }] // What should the hospital do?
}, { timestamps: true });

module.exports = mongoose.model('Prediction', predictionSchema);
